// Dependencies
import React, { Component } from 'react';
import { Map, Marker, Popup, TileLayer } from 'react-leaflet';
import MarkerClusterGroup from 'react-leaflet-markercluster';


var marker_list = {lat: {}, long: {}};


class BMap extends Component {

  componentDidMount(){
    var token = JSON.parse(localStorage.getItem('token'));

    var url = "https://api.mercadolibre.com/orders/search?seller="+ token.user_id +"&order.status=paid&access_token="+ token.access_token;

    var cont = 0;

    fetch(url)
      .then(function(response){
        return response.json();
      })
      .then(function(data){
        for (var i = 0; i < data.results.length; i++) {
          if (data.results[i].shipping.receiver_address !== undefined) {
            if (data.results[i].shipping.receiver_address.latitude !== null) {

              marker_list.lat[cont] = data.results[i].shipping.receiver_address.latitude;
              marker_list.long[cont] = data.results[i].shipping.receiver_address.longitude
              cont++;

              //console.log(data.results[i].shipping.receiver_address.latitude);
              //console.log(data.results[i].shipping.receiver_address.longitude);
              //console.log('--------------------')  
            }
          }
        }
        localStorage.setItem('markerList',marker_list);
        console.log(marker_list[0],marker_list[1],marker_list[2],marker_list[3],marker_list[4]);
      })
  }

  render() {
    var ml = localStorage.getItem('markerList');

    return (
      <div className="BMap">
        <h1 style={{textAlign: 'center'}}>MAP PAGE</h1>

        <div>
          <Map style={{ display: 'block',marginLeft: 'auto',marginRight: 'auto',height: '500px', width: '700px' }} center={[51.505, -0.09]} zoom={13} maxZoom={17}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            />
            <MarkerClusterGroup maxClusterRadius={120}>
              <Marker position={[0,0/*ml.lat[0], ml.long[0]*/]}>
                <Popup>Marker 1</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[1], ml.long[1]*/]}>
                <Popup>Marker 2</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[2], ml.long[2]*/]}>
                <Popup>Marker 3</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[3], ml.long[3]*/]}>
                <Popup>Marker 4</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[4], ml.long[4]*/]}>
                <Popup>Marker 5</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[5], ml.long[5]*/]}>
                <Popup>Marker 6</Popup>
              </Marker>
              <Marker position={[0,0/*ml.lat[6], ml.long[6]*/]}>
                <Popup>Marker 7</Popup>
              </Marker>
            </MarkerClusterGroup>
          </Map>
        </div>
      </div>
    );
  }
}

export default (BMap);
