    "redirect_uri": 'http://localhost:3000/logued_in',
    "code": ""}; 

var options = {
   url:'https://api.mercadolibre.com/oauth/token', 
   form: valores, 
   method: "POST",
   headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    'Accept': 'application/json' 
   }
};


