import React, { Component } from 'react';
import { parse } from 'query-string';
import { tsParameterProperty } from '@babel/types';

var options = {
   form: {
   "grant_type":"authorization_code",
   "client_id": '4069477448135367',
   "client_secret": 'eqaPB8Ot1neu4JVVGyqDu5tPorwvmlh2',
   "redirect_uri": 'http://localhost:3000/logued_in',
   "code": ""}, 
   method: "POST",
   headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    'Accept': 'application/json' 
   }
};

const method = {method: "POST"}

const burl = 'https://api.mercadolibre.com/oauth/token' + $.param({grant_type:"authorization_code",client_id: '4069477448135367',client_secret: 'eqaPB8Ot1neu4JVVGyqDu5tPorwvmlh2',redirect_uri: 'http://localhost:3000/logued_in',code: ""});


class LoguedIn extends Component {

  componentDidMount(){

    fetch(burl,method,grant_type,options.form,options.headers)
    .then(function(response){
      response.text()
      .then(function(data) {
        console.log(data);
      });
    })
    .catch(function(error) {
      console.log('Fetch Error:', error);
    });
  }

  render() {
    options.form.code = parse(this.props.location.search).code
    console.log(options.form.code);
    return (
      <div className="LoguedIn">
        <h1 style={{textAlign: 'center'}}>CONTACT PAGE</h1>
        <button style={{textAlign: 'center',display: 'block',marginLeft: 'auto',marginRight: 'auto',height: '10%', width: '10%'}}>Mapa</button>
      </div>
    );
  }
}

export default LoguedIn;
